import osimport shutilimport datetimedef datem(filename):    Q = os.path.getmtime(filename)    return datetime.datetime.fromtimestamp(Q)r = input('Путь:')s = int(input('Макс размер:'))d = int(input('Старость дока:'))kat = [f for f in os.listdir(r) if os.path.isfile(os.path.join(r, f))]r1 = r + 'Archive/'r2 = r + 'Small/'dtf = '%Y-%m-%d %H:%M:%S.%f'now = datetime.datetime.now()print('------------Это в Archive------------')for file in kat:    t = datem(file)    timedelta = datetime.datetime.strptime(str(now), dtf) - datetime.datetime.strptime(str(t), dtf)    if timedelta.days > d:        print(file)        arch = os.path.dirname(r1)        try:            os.stat(arch)        except:            os.mkdir(arch)        shutil.move(file, r1)kat = [f for f in os.listdir(r) if os.path.isfile(os.path.join(r, f))]print('------------Это в Small------------')for file in kat:    sz = os.path.getsize(file)    if sz < s:        print(file)        sm = os.path.dirname(r2)        try:            os.stat(sm)        except:            os.mkdir(sm)        shutil.move(file, r2)